g1 %>% ggplot(aes(x = FULL_NAME, y = COMMITTEE_ID, color = CHAMBER)) + geom_point() + ggtitle('Committees and Members') + theme(plot.title=element_text(size=20, face="bold", vjust=2)) + labs(x=paste("Full", "Name"), y=paste("Committee", "ID")) + theme(axis.text.x=element_text(angle=90, size=3),axis.text.y=element_text(size=4))
g2 %>% ggplot(aes(x = COMMITTEE_ID, y = n, color = ACTIVE)) + geom_point() + ggtitle('Number of Actives and Inactives in Each Committee') + theme(plot.title=element_text(size=20, face="bold", vjust=2)) + labs(x=paste("Committee", "ID"), y=paste("Number", "of", "Members")) + theme(axis.text.x=element_text(angle=70, size=5, vjust=0.6))
g3 %>% ggplot(aes(x = FULL_NAME, y = TERM, color = PARTY.x)) + geom_point() + ggtitle('Term and Party of Non-Committee Members') + theme(plot.title=element_text(size=20, face="bold", vjust=2)) + labs(x=paste("Full", "Name"), y=("Term")) + theme(axis.text.x=element_text(angle=70, size=10, vjust=0.6)) + scale_colour_manual(name='Party',values=c('Republican'='red','Democratic'='blue')) + ylim(c(80,84))